content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const departments = await db.department.findMany({
    where: {
      id_vendor: user.user.dataUser.id_vendor,
    },
  });
  reply.send({
    data: departments,
  })
}
