content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_ticket,
    id_level_ticket,
    id_status_ticket,
    id_department,
  } = req.body;
  await db.history_ticket.update({
    where: {
      id_ticket,
    },
    data: {
      id_level_ticket,
      id_status_ticket,
      id_department,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
