content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_ticket,
    id_level_ticket,
    id_status_ticket,
    id_department,
    tanggal_assign,
    id_history_ticket,
  } = req.body;

  const employee = await db.employee.findMany({
    where: {
      id_department,
    },
  });

  await db.history_ticket.update({
    where: {
      id_ticket,
    },
    data: {
      id_level_ticket,
      id_status_ticket,
      id_department,
      tanggalAssign_ticket: tanggal_assign,
    },
  });

  for (let i = 0; i < employee.length; i++) {
    await db.notifikasi.create({
      data: {
        id_historyTicket: id_history_ticket,
        status_notifications: "Diterima",
        type_notification: "Ticket Assign",
        date_notification: tanggal_assign,
        id_user: employee[i].id_user,
      },
    });
  }

  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
