content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_vendor,
    nama_client,
    email_client,
    lokasi_client,
    nomor_client,
    perusahaan_client,
  } = req.body;
  const usern = nama_client.replace(/\s/g, "");
  const username = usern.toLowerCase();
  const password = await ext.argon2.hash(nomor_client);
  const dataUser = await db.users.create({
    data: {
      id_role: 3,
      username,
      password,
    },
  });
  await db.client.create({
    data: {
      id_vendor,
      id_user: dataUser.id_user,
      nama_client,
      email_client,
      lokasi_client,
      nomor_client,
      perusahaan_client,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
