content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_vendor,
    id_department,
    nama_employee,
    nomor_employee,
    email_employee,
    shift_kerja,
  } = req.body;
  const usern = nama_employee.replace(/\s/g, "");
  const username = usern.toLowerCase();
  const password = await ext.argon2.hash(nomor_employee);
  const dataUser = await db.users.create({
    data: {
      id_role: 4,
      username,
      password,
    },
  });
  await db.employee.create({
    data: {
      id_department,
      id_user: dataUser.id_user,
      nama_employee,
      nomor_employee,
      email_employee,
      shift_kerja,
      id_vendor,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
