content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_user, nama, email, company, nomor, address, username } = req.body;
  const namaRole = user.user.role;
  var dataUser = {};
  const users = await db.users.findUnique({
    where: {
      username: username,
    },
  });
  if (!users) {
    await db.users.update({
      where: {
        id_user,
      },
      data: {
        username,
        email,
      },
    });
  } else {
    if (users.id_user !== id_user) {
      return reply.status(404).send({
        status: "ERROR",
        message:
          "The username you entered has been used. Please enter a different username..",
      });
    } else {
      await db.users.update({
        where: {
          id_user,
        },
        data: {
          username,
          email,
        },
      });
    }
  }
  if (user.user.role === "Vendor") {
    dataUser = await db.vendor.update({
      where: {
        id_user,
      },
      data: {
        nama_vendor: nama,
        lokasi_vendor: address,
        nomor_vendor: nomor,
        nama_perusahaan: company,
      },
    });
  }
  if (user.user.role === "Client") {
    dataUser = await db.client.update({
      where: {
        id_user,
      },
      data: {
        nama_client: nama,
        lokasi_client: address,
        nomor_client: nomor,
        perusahaan_client: company,
      },
    });
  }
  if (user.user.role === "Employee") {
    dataUser = await db.employee.update({
      where: {
        id_user,
      },
      data: {
        nama_employee: nama,
        nomor_employee: nomor,
        lokasi_employee: address,
      },
    });
  }

  user.user = { role: namaRole, dataUser };
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
