content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_user, nama, email, lokasi, nomor, username } = req.body;
  const namaRole = user.user.role;
  var dataUser = {};
  await db.users.update({
    where: {
      id_user,
    },
    data: {
      username,
    },
  });
  if (user.user.role === "Vendor") {
    dataUser = await db.vendor.update({
      where: {
        id_user,
      },
      data: {
        nama_vendor: nama,
        email_vendor: email,
        lokasi_vendor: lokasi,
        nomor_vendor: nomor,
      },
    });
  }
  if (user.user.role === "Client") {
    dataUser = await db.client.update({
      where: {
        id_user,
      },
      data: {
        nama_client: nama,
        email_client: email,
        lokasi_client: lokasi,
        nomor_client: nomor,
      },
    });
  }
  if (user.user.role === "Employee") {
    dataUser = await db.employee.update({
      where: {
        id_user,
      },
      data: {
        nama_employee: nama,
        email_employee: email,
        nomor_employee: nomor,
      },
    });
  }

  user.user = { role: namaRole, dataUser };
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
