content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_employee,
    id_department,
    id_user,
    nama_employee,
    nomor_employee,
    email_employee,
    shift_kerja,
    id_vendor,
  } = req.body;
  await db.employee.update({
    where: {
      id_employee,
    },
    data: {
      id_department,
      id_user,
      nama_employee,
      nomor_employee,
      email_employee,
      shift_kerja,
      id_vendor,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
