content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_notif, task } = req?.body;
  let notifications, total;

  if (task === "all") {
    notifications = await db.notifikasi.findMany({
      where: {
        id_user: user.user.dataUser.id_user,
      },
      include: {
        history_ticket: {
          include: {
            ticket: true,
          },
        },
      },
    });

    total = await db.notifikasi.count({
      where: {
        id_user: user.user.dataUser.id_user,
      },
    });
  }

  if (task === "read all") {
    await db.notifikasi.updateMany({
      where: {
        id_user: user.user.dataUser.id_user,
      },
      data: {
        status_notifications: "Dibaca",
      },
    });
  }

  if (task === "read") {
    await db.notifikasi.update({
      where: {
        id_notification: id_notif,
      },
      data: {
        status_notifications: "Dibaca",
      },
    });
  }

  reply.send({
    status: "SUCCESS",
    data: notifications,
    count: total,
  })
}
