content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { task } = req?.body;

  let notifications;
  let total;

  if (task === "news") {
    notifications = await db.notifikasi.findMany({
      where: {
        id_user: user.user.dataUser.id_user,
        status_notifications: "Diterima",
      },
    });

    total = await db.notifikasi.count({
      where: {
        id_user: user.user.dataUser.id_user,
        status_notifications: "Diterima",
      },
    });
  }

  if (task === "all") {
    notifications = await db.notifikasi.findMany({
      where: {
        id_user: user.user.dataUser.id_user,
      },
    });

    total = await db.notifikasi.count({
      where: {
        id_user: user.user.dataUser.id_user,
      },
    });
  }

  reply.send({
    data: notifications,
    count: total,
  })
}
