content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const notifications = await db.notifikasi.findMany({
    where: {
      id_user: user.user.dataUser.id_user,
    },
  });

  const total = await db.notifikasi.count({
    where: {
      id_user: user.user.dataUser.id_user,
    },
  });

  reply.send({
    data: notifications,
    count: total,
  })
}
