content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { jenis_ticket, deskripsi_jenis_ticket } = req.body;
  await db.jenis_ticket.create({
    data: {
      jenis_ticket,
      deskripsi_jenis_ticket,
      id_vendor: user.user.dataUser.id_vendor,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
