content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_ticket,
    tanggal_solved,
    id_status_ticket,
    id_history_ticket,
    email,
    id_user,
  } = req.body;

  const message =
    "There is a new ticket that has just been Resolved, check it immediately. \n Here's your ticket list: http://localhost:3200/ticket-data";
  var timeDifference;

  const ticket = await db.history_ticket.findUnique({
    where: {
      id_history_ticket,
    },
  });

  if (ticket.tanggalProgress_ticket !== null) {
    timeDifference = ext.hoursDifference(
      tanggal_solved,
      ticket.tanggalProgress_ticket
    );
  } else {
    timeDifference = 0;
  }

  await db.history_ticket.update({
    where: {
      id_ticket,
    },
    data: {
      tanggalSolved_ticket: tanggal_solved,
      id_status_ticket,
      ticketSolved_deviation: timeDifference,
      solvedDay: tanggal_solved,
    },
  });

  await db.notifikasi.create({
    data: {
      id_historyTicket: id_history_ticket,
      status_notifications: "Diterima",
      type_notification: "Ticket Solved",
      date_notification: tanggal_solved,
      id_user,
    },
  });
  await ext.sendNotif(email, message);
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
