content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_ticket, tanggal_solved, id_status_ticket } = req.body;
  const date = ext.format(tanggal_solved);
  await db.history_ticket.update({
    where: {
      id_ticket,
    },
    data: {
      tanggalSolved_ticket: date,
      id_status_ticket,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
