content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { email, task } = req.body;
  var verifToken;
  if (task === "web") {
    verifToken = ext.generateLongToken();
  } else {
    verifToken = ext.generateToken();
  }

  const users = await db.users.findUnique({
    where: {
      email,
    },
  });

  if (!users) {
    return reply.status(404).send({
      status: "ERROR",
      message:
        "Your account hasnâ€™t registered yet. Make sure the email you entered is correct.",
    });
  }

  if (task === "web") {
    await ext.sendVerifEmail(email, verifToken);
    await db.users.update({
      where: {
        email,
      },
      data: {
        token: verifToken,
      },
    });
  } else {
    await ext.sendToken(email, verifToken);
    var dataUser = await db.users.update({
      where: {
        email,
      },
      data: {
        token: verifToken,
      },
    });

    user.user = { role: "Guest", dataUser };
  }

  reply.status(200).send({
    status: "SUCCESS",
    message: "Berhasil login",
    data: verifToken,
  })
}
