content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const clients = await db.client.findMany({
    where: {
      id_vendor: user.user.dataUser.id_vendor,
    },
    include: {
      users: true,
      vendor: true,
    },
  });
  reply.send({
    data: clients,
  })
}
