content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { code } = req.body;

  const users = await db.users.findUnique({
    where: {
      email: user.user.dataUser.email,
    },
  });

  if (users.token != code) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Wrong code. Make sure the code you entered is correct.",
    });
  }

  reply.status(200).send({
    status: "SUCCESS",
    message: "OTP Success",
  })
}
