content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    judul_ticket,
    deskripsi_ticket,
    tanggal_masuk,
    attachment,
    id_jenis_ticket,
    id_status_ticket,
    id_client,
    id_vendor,
    id_notif,
    email,
  } = req.body;
  const message =
    "There is a new ticket that has just been opened, immediately check and assign it to the appropriate department. \n Here's your ticket list: http://localhost:3200/ticket-data";
  const dataTicket = await db.ticket.create({
    data: {
      judul_ticket,
      deskripsi_ticket,
      attachment,
      id_jenis_ticket,
    },
  });
  const dataHistoryTicket = await db.history_ticket.create({
    data: {
      id_ticket: dataTicket.id_ticket,
      tanggalMasuk_ticket: tanggal_masuk,
      id_status_ticket,
      id_client,
      id_vendor,
    },
  });
  await db.notifikasi.create({
    data: {
      id_historyTicket: dataHistoryTicket.id_history_ticket,
      status_notifications: "Diterima",
      type_notification: "Ticket Entrance",
      date_notification: tanggal_masuk,
      id_user: id_notif,
    },
  });
  await ext.sendNotif(email, message);
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
