content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    nama_client,
    jenis_ticket,
    judul_ticket,
    deskripsi_ticket,
    tanggal_masuk,
    attachment,
  } = req.body;

  const name = await db.client.findMany({
    data: {
      nama_client,
    },
  });
  const jenis = await db.jenis_ticket.findMany({
    data: {
      jenis_ticket,
    },
  });

  await db.ticket.create({
    data: {
      name,
      jenis,
      judul_ticket,
      deskripsi_ticket,
      tanggal_masuk,
      attachment,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
