content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { data, task } = req?.body;

  if (task === "change") {
    const users = await db.users.findUnique({
      where: {
        id_user: data.id_user,
      },
    });
    const pass_verify = await ext.argon2.verify(users.password, data.password);
    if (!pass_verify) {
      return reply.status(403).send({
        status: "ERROR",
        message: "Password you entered is incorrect.",
      });
    }
    const passwordEncrypt = await ext.argon2.hash(data.newPassword);
    if (data.newPassword === data.confirmPassword) {
      await db.users.update({
        where: {
          id_user: data.id_user,
        },
        data: {
          password: passwordEncrypt,
        },
      });
    } else {
      return reply.status(403).send({
        status: "ERROR",
        message: "Your new password isn't same.",
      });
    }
  }

  if (task === "forgot") {
    const users = await db.users.findUnique({
      where: {
        token: data.token,
      },
    });

    if (!users) {
      return reply.status(404).send({
        status: "ERROR",
        message:
          "Your token has expired, please enter your email on the forgot password page again.",
      });
    }

    if (data.newPassword === data.confirmPassword) {
      const passwordEncrypt = await ext.argon2.hash(data.newPassword);
      await db.users.update({
        where: {
          token: data.token,
        },
        data: {
          password: passwordEncrypt,
          token: "None",
        },
      });
    } else {
      return reply.status(403).send({
        status: "ERROR",
        message: "Your new password isn't same.",
      });
    }
  }

  if (task === "otp") {
    const users = await db.users.findUnique({
      where: {
        email: user.user.dataUser.email,
      },
    });

    if (!users) {
      return reply.status(404).send({
        status: "ERROR",
        message:
          "Your token has expired, please enter your email on the forgot password page again.",
      });
    }

    if (data.newPassword === data.confirmPassword) {
      const passwordEncrypt = await ext.argon2.hash(data.newPassword);
      await db.users.update({
        where: {
          email: user.user.dataUser.email,
        },
        data: {
          password: passwordEncrypt,
          token: "None",
        },
      });
    } else {
      return reply.status(403).send({
        status: "ERROR",
        message: "Your new password isn't same.",
      });
    }
  }

  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
