content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_user, password, newPassword, confirmPassword } = req.body;
  const users = await db.users.findUnique({
    where: {
      id_user,
    },
  });
  const pass_verify = await ext.argon2.verify(users.password, password);
  if (!pass_verify) {
    return reply.status(403).send({
      status: "ERROR",
      message: "Password you entered is incorrect.",
    });
  }
  const passwordEncrypt = await ext.argon2.hash(newPassword);
  if (newPassword === confirmPassword) {
    await db.users.update({
      where: {
        id_user,
      },
      data: {
        password: passwordEncrypt,
      },
    });
  } else {
    return reply.status(403).send({
      status: "ERROR",
      message: "Your new password isn't same.",
    });
  }

  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
