content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_vendor,
    id_user,
    nama_vendor,
    email_vendor,
    lokasi_vendor,
    nomor_vendor,
    nama_perusahaan,
  } = req.body;
  await db.vendor.update({
    where: {
      id_vendor,
    },
    data: {
      id_user,
      nama_vendor,
      lokasi_vendor,
      nomor_vendor,
      nama_perusahaan,
    },
  });

  await db.users.update({
    where: {
      id_user,
    },
    data: {
      email: email_vendor,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
