content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    id_client,
    id_vendor,
    id_user,
    nama_client,
    email_client,
    lokasi_client,
    nomor_client,
    perusahaan_client,
  } = req.body;
  await db.client.update({
    where: {
      id_client,
    },
    data: {
      id_vendor,
      id_user,
      nama_client,
      lokasi_client,
      nomor_client,
      perusahaan_client,
    },
  });

  await db.users.update({
    where: {
      id_user,
    },
    data: {
      email: email_client,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
