content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama_vendor, email_vendor, lokasi_vendor, nomor_vendor } = req.body;
  const usern = nama_vendor.replace(/\s/g, "");
  const username = usern.toLowerCase();
  const password = await ext.argon2.hash(nomor_vendor);
  const dataUser = await db.users.create({
    data: {
      id_role: 2,
      username,
      password,
    },
  });
  await db.vendor.create({
    data: {
      id_user: dataUser.id_user,
      nama_vendor,
      email_vendor,
      lokasi_vendor,
      nomor_vendor,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
