content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_client, id_user } = req.body;
  await db.client.delete({
    where: {
      id_client,
    },
  });
  await db.users.delete({
    where: {
      id_user,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
