content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama_department, deskripsi, identityColor } = req.body;
  await db.department.create({
    data: {
      nama_department,
      deskripsi,
      id_vendor: user.user.dataUser.id_vendor,
      identityColor,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
