content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama_department, deskripsi } = req.body;
  await db.department.create({
    data: {
      nama_department,
      deskripsi,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil tambah data",
  })
}
