content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_jenis_ticket, jenis_ticket, deskripsi_jenis_ticket } = req.body;
  await db.jenis_ticket.update({
    where: {
      id_jenis_ticket,
    },
    data: {
      jenis_ticket,
      deskripsi_jenis_ticket,
    },
  });
  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data",
  })
}
