content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  var stats = [];
  var high = await db.history_ticket.count({
    where: {
      id_level_ticket: 1,
    },
  });
  var medium = await db.history_ticket.count({
    where: {
      id_level_ticket: 2,
    },
  });
  var low = await db.history_ticket.count({
    where: {
      id_level_ticket: 3,
    },
  });
  stats = [high, medium, low];
  reply.send({
    data: stats,
  })
}
