content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { dateLevel } = req.body;
  var myDate;
  var stats = [];

  if (dateLevel === null) {
    myDate = new Date();
  } else {
    myDate = dateLevel;
  }

  // For Vendor
  if (user.user.role === "Vendor") {
    var high = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 1,
            solvedDay: myDate,
            id_vendor: user.user.dataUser.id_vendor,
          },
          {
            id_level_ticket: 1,
            solvedDay: null,
            id_vendor: user.user.dataUser.id_vendor,
          },
        ],
      },
    });
    var medium = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 2,
            solvedDay: myDate,
            id_vendor: user.user.dataUser.id_vendor,
          },
          {
            id_level_ticket: 2,
            solvedDay: null,
            id_vendor: user.user.dataUser.id_vendor,
          },
        ],
      },
    });
    var low = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 3,
            solvedDay: myDate,
            id_vendor: user.user.dataUser.id_vendor,
          },
          {
            id_level_ticket: 3,
            solvedDay: null,
            id_vendor: user.user.dataUser.id_vendor,
          },
        ],
      },
    });
    stats = [high, medium, low];
  }

  //For Employee
  if (user.user.role === "Employee") {
    var high = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 1,
            solvedDay: myDate,
            id_employee: user.user.dataUser.id_employee,
          },
          {
            id_level_ticket: 1,
            solvedDay: null,
            id_employee: user.user.dataUser.id_employee,
          },
        ],
      },
    });
    var medium = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 2,
            solvedDay: myDate,
            id_employee: user.user.dataUser.id_employee,
          },
          {
            id_level_ticket: 2,
            solvedDay: null,
            id_employee: user.user.dataUser.id_employee,
          },
        ],
      },
    });
    var low = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 3,
            solvedDay: myDate,
            id_employee: user.user.dataUser.id_employee,
          },
          {
            id_level_ticket: 3,
            solvedDay: null,
            id_employee: user.user.dataUser.id_employee,
          },
        ],
      },
    });
    stats = [high, medium, low];
  }

  //For Client
  if (user.user.role === "Client") {
    var high = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 1,
            solvedDay: myDate,
            id_employee: user.user.dataUser.id_employee,
            id_client: user.user.dataUser.id_client,
          },
          {
            id_level_ticket: 1,
            solvedDay: null,
            id_employee: user.user.dataUser.id_employee,
            id_client: user.user.dataUser.id_client,
          },
        ],
      },
    });
    var medium = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 2,
            solvedDay: myDate,
            id_employee: user.user.dataUser.id_employee,
            id_client: user.user.dataUser.id_client,
          },
          {
            id_level_ticket: 2,
            solvedDay: null,
            id_employee: user.user.dataUser.id_employee,
            id_client: user.user.dataUser.id_client,
          },
        ],
      },
    });
    var low = await db.history_ticket.count({
      where: {
        OR: [
          {
            id_level_ticket: 3,
            solvedDay: myDate,
            id_employee: user.user.dataUser.id_employee,
            id_client: user.user.dataUser.id_client,
          },
          {
            id_level_ticket: 3,
            solvedDay: null,
            id_employee: user.user.dataUser.id_employee,
            id_client: user.user.dataUser.id_client,
          },
        ],
      },
    });
    stats = [high, medium, low];
  }

  reply.send({
    data: stats,
  })
}
