content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const employees = await db.employee.findMany({
    include: {
      vendor: true,
      department: true,
      users: true,
    },
  });
  reply.send({
    data: employees,
  })
}
